 import axios from 'axios';
import { useState } from "react";
import "./App.css";

function App() {
  const [data, setData] = useState({});
  const [lacation, setLocation] = useState('')
  const url =`https://api.openweathermap.org/data/2.5/weather?q=${lacation}&appid=7b1f50987ce7a83985019be4da6977a3`;

  const searchLocation = () =>{
    axios.get(url).then((response) => {
      setData(response.data)
      console.log(response.data);
    })
  }

  return (
    <div className="app">
      <div className="container">
        <div className="top">
          <div className="location">
            <p>sanandaj</p>
          </div>
          <div className="temp">
            <h1>60ºF</h1>
          </div>
          <div className="description">
            <p>Clouds</p>
          </div>
        </div>
        <div className="bottom">
          <div className="feels">
            <p className="bold"> 65ºF</p>
            <p>feels like</p>
          </div>
          <div className="humidity">
            <p className="bold">20%</p>
            <p>Humidity</p>
          </div>
          <div className="wind">
            <p className="bold">12 MPH</p>
            <p>wind speed</p>
          </div>
        </div>
      </div>
      <div></div>
    </div>
  );
}

export default App;
